window.addEventListener("load",()=>{let a=$("#cities"),d=$("[data-country-id]");d.click(t=>{t.preventDefault();var e=$(t.currentTarget),e=(d.removeClass("disabled"),d.attr("disabled",null),e.addClass("disabled"),e.attr("disabled",!0),t.currentTarget.dataset.countryId);$.ajax({url:"/country/cities?id="+e,method:"GET",success:function(t){t=JSON.parse(t);if(Array.isArray(t)){let e="";t.forEach(t=>{e+="<tr><td>"+t.name+"</td></tr>"}),a.find("tbody").html(e),a.removeClass("d-none")}}})})});